<?phprequire_once 'admin/init.php';require_once 'includes/cron.php';require_once 'includes/enqueue.php';function uis_sales_login() {    $nonce = isset( $_POST['sales_login_form'] ) ? $_POST['sales_login_form'] : false;     if( wp_verify_nonce( $nonce, 'sales_login_form' ) ) {//    die( 'sariel' );    $user = wp_signon( array(      'user_login' => sanitize_user( $_POST[ 'sales_login_username' ] ),      'user_password' => sanitize_text_field( $_POST[ 'sales_login_password' ] )    ) );    if ( is_wp_error( $user ) ){      wp_redirect( '/sales-login/' );      die;    } else {      foreach($user->roles as $role) {        if ( $role == "uis_sales" || $role == 'administrator' ) {          wp_redirect( '/sales-dashboard/' ); die;        } else {          wp_logout();        }      }    }  } else {    die;  }}add_action( 'admin_post_sales_login_form', 'uis_sales_login' );//add_action( 'admin_post', 'uis_sales_login' );//add_action( 'admin_post_nopriv', 'uis_sales_login' );add_action( 'admin_post_nopriv_sales_login_form', 'uis_sales_login' );